mapper.select:
SELECT count(*) FROM task_member AS tm
JOIN task AS t ON t.task_seq = tm.task_seq
WHERE tm.projmember_seq = #{projmember_seq} AND project_seq = #{project_seq}

SELECT count(*) FROM task_member AS tm
    JOIN task AS t ON t.task_seq = tm.task_seq
WHERE t.task_status = '1' AND tm.projmember_seq = #{projmember_seq} AND project_seq = #{project_seq}
