mapper.update:
UPDATE project
SET project_status = #{project_status}
WHERE project_seq = #{project_seq}

UPDATE project
SET project_isdelete = #{project_isdelete}
WHERE project_seq = #{project_seq}

UPDATE project
SET project_title = #{project_title}
WHERE project_seq = #{project_seq}

UPDATE project
SET project_content = #{project_content}
WHERE project_seq = #{project_seq}

UPDATE project
SET project_enddate = now()
WHERE project_seq = #{project_seq}
